// src/components/AIPanel/AiSummarySection.jsx
import React from 'react';

export default function AiSummarySection({ summaryText, isLoading }) {
  if (isLoading) {
    return (
      <section className="ai-section page-break-after">
        <h3 className="ai-section-title">Resumo Executivo (Análise IA) ✨</h3>
        <div className="animate-pulse text-slate-600">
          <div className="h-4 bg-slate-200 rounded w-3/4 mb-2"></div>
          <div className="h-4 bg-slate-200 rounded w-full mb-2"></div>
          <div className="h-4 bg-slate-200 rounded w-5/6"></div>
        </div>
      </section>
    );
  }

  if (!summaryText || summaryText.startsWith("Falha") || summaryText.startsWith("Erro:")) {
    return (
      <section className="ai-section page-break-after">
        <h3 className="ai-section-title">Resumo Executivo (Análise IA) ✨</h3>
        <p className="text-orange-600 text-sm">{summaryText || "Nenhum resumo gerado."}</p>
      </section>
    );
  }

  return (
    <section className="ai-section page-break-after">
      <h3 className="ai-section-title">Resumo Executivo (Análise IA) ✨</h3>
      <div
        className="prose prose-sm max-w-none text-slate-700 whitespace-pre-wrap"
        dangerouslySetInnerHTML={{ __html: summaryText.replace(/\n/g, '<br />') }}
      ></div>
    </section>
  );
}
