// src/components/AIPanel/AIPanel.jsx
import React, { useState, useEffect } from 'react';
import { useGeminiApi } from '../../hooks/useGeminiApi';
import AiSummarySection from './AiSummarySection';
import { GENAI_API_KEY_PLACEHOLDER } from '../../utils/constants';
import { PERIOD_TYPES } from '../../utils/constants';
import { formatCurrency } from '../../utils/formatters';

export default function AIPanel({ calculatedData, companyInfo }) {
  const [apiKey, setApiKey] = useState(localStorage.getItem('geminiApiKey') || '');
  const [summaryText, setSummaryText] = useState('');
  const [apiKeyModalOpen, setApiKeyModalOpen] = useState(false);
  const { callApi, isLoading, error, resetError } = useGeminiApi();

  // Auto-generate insights when calculated data is provided
  useEffect(() => {
    if (calculatedData && calculatedData.length > 0 && apiKey && apiKey !== GENAI_API_KEY_PLACEHOLDER) {
      handleGenerateInsights();
    }
  }, [calculatedData, apiKey]);

  // Check if we need to show API key modal on mount
  useEffect(() => {
    if (!apiKey || apiKey === GENAI_API_KEY_PLACEHOLDER) {
      setApiKeyModalOpen(true);
    }
  }, []);

  const buildPrompt = () => {
    // Create a detailed prompt for the AI with calculated data
    const { name, reportTitle, periodType } = companyInfo;
    const periodLabel = PERIOD_TYPES[periodType]?.label || 'períodos';

    // Format financial data for the AI prompt
    const financialSummary = calculatedData.map((period, idx) => {
      return `
Período ${idx + 1}:
- Receita: ${formatCurrency(period.revenue)}
- Margem Bruta: ${period.gmPct.toFixed(2)}%
- EBITDA: ${formatCurrency(period.ebitda)} (${(period.ebitda / period.revenue * 100).toFixed(2)}% da receita)
- Lucro Líquido: ${formatCurrency(period.netProfit)} (${period.netProfitPct.toFixed(2)}% da receita)
- Fluxo de Caixa Operacional: ${formatCurrency(period.operatingCashFlow)}
- Fluxo Líquido de Caixa: ${formatCurrency(period.netChangeInCash)}
- Capital de Giro: ${formatCurrency(period.workingCapitalValue)} (${period.wcDays.toFixed(1)} dias)
- Saldo Final de Caixa: ${formatCurrency(period.closingCash)}`;
    }).join('\n\n');

    // Create the prompt
    return `
Você é um analista financeiro especializado em análise de fluxo de caixa corporativo para diretoria executiva e investidores.

Empresa: ${name}
Título do Relatório: ${reportTitle}
Períodos: ${calculatedData.length} ${periodLabel}

DADOS FINANCEIROS:
${financialSummary}

INSTRUÇÕES:
1. Crie um resumo executivo conciso (máx. 2-3 parágrafos) sobre a situação financeira da empresa.
2. Destaque as principais tendências observadas nos dados dos períodos.
3. Mencione especificamente o desempenho do fluxo de caixa, EBITDA e as necessidades de capital de giro.
4. Identifique possíveis riscos ou oportunidades com base nos números apresentados.
5. Foque principalmente em aspectos práticos e conclusões objetivas baseadas apenas nos dados fornecidos.
6. Conclua com 1-2 recomendações concretas para a gestão financeira da empresa.

Por favor, formule o resumo em português formal, adequado para uma apresentação de diretoria.`;
  };

  const handleGenerateInsights = async () => {
    if (!apiKey) {
      setApiKeyModalOpen(true);
      return;
    }

    resetError();
    setSummaryText('');

    try {
      const prompt = buildPrompt();
      console.log('Prompt gerado para IA:', prompt);

      const aiResponse = await callApi(prompt, apiKey);
      setSummaryText(aiResponse);
    } catch (err) {
      console.error('Erro ao gerar insights:', err);
      setSummaryText(`Falha ao gerar insights: ${err.message}`);
    }
  };

  const handleSaveApiKey = () => {
    localStorage.setItem('geminiApiKey', apiKey);
    setApiKeyModalOpen(false);
    if (calculatedData && calculatedData.length > 0) {
      handleGenerateInsights();
    }
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-bold text-slate-800">Análise de IA</h2>
        <div className="flex gap-2">
          <button
            onClick={() => setApiKeyModalOpen(true)}
            className="px-3 py-1 text-sm bg-slate-200 text-slate-700 rounded hover:bg-slate-300 transition"
          >
            Configurar API
          </button>
          <button
            onClick={handleGenerateInsights}
            disabled={isLoading || !apiKey || apiKey === GENAI_API_KEY_PLACEHOLDER}
            className="px-3 py-1 text-sm bg-green-600 text-white rounded hover:bg-green-700 transition disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isLoading ? 'Gerando...' : 'Gerar Novo Insight'}
          </button>
        </div>
      </div>

      {error && (
        <div className="mb-4 p-3 bg-red-100 border border-red-300 rounded-md">
          <p className="text-sm text-red-700">Erro na API: {error.message}</p>
        </div>
      )}

      <AiSummarySection summaryText={summaryText} isLoading={isLoading} />

      {/* API Key Modal */}
      {apiKeyModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Configurar API Key do Google Gemini</h3>
            <p className="text-sm text-slate-600 mb-4">
              Para utilizar o recurso de IA, é necessário fornecer sua API key do Google Gemini.
              <a 
                href="https://ai.google.dev/tutorials/setup" 
                target="_blank" 
                rel="noopener noreferrer"
                className="text-blue-600 hover:text-blue-800 ml-1"
              >
                Como obter uma API key?
              </a>
            </p>

            <div className="mb-4">
              <label htmlFor="apiKey" className="block text-sm font-medium text-slate-700 mb-1">
                API Key:
              </label>
              <input
                type="text"
                id="apiKey"
                value={apiKey}
                onChange={(e) => setApiKey(e.target.value)}
                placeholder="Digite sua Google Gemini API Key"
                className="w-full p-2 border border-slate-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>

            <div className="flex justify-end gap-3">
              <button
                onClick={() => setApiKeyModalOpen(false)}
                className="px-4 py-2 bg-slate-200 text-slate-700 rounded hover:bg-slate-300"
              >
                Cancelar
              </button>
              <button
                onClick={handleSaveApiKey}
                disabled={!apiKey || apiKey === GENAI_API_KEY_PLACEHOLDER}
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Salvar e Usar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
